var ERR_STRs = {
    usernameErr:    '以字母开头的6~18位英文字母、数字或_',
    idErr:          '8位数字，不能以0开头',
    telErr:         '11位数字，不能以0开头',
    emailErr:       '邮箱不合法'
}, EXI_STRs = {
    usernameErr:    '用户名已存在',
    idErr:          '学号已存在',
    telErr:         '电话已存在',
    emailErr:       '邮箱已存在'
};

// 1 for exists, -1 for invalid
function check(param) {
    var err = {
        code: 0,
        usernameErr: false,
        idErr: false,
        telErr: false,
        emailErr: false
    };
    if (!param.username.match(/^[a-zA-Z][a-zA-Z0-9_]{5,17}$/)) {
        err.code += 1;
        err.usernameErr = true;
    }
    if (!param.id.match(/^[1-9][0-9]{7}$/)) {
        err.code += 2;
        err.idErr = true;
    }
    if (!param.tel.match(/^[1-9][0-9]{10}$/)) {
        err.code += 4;
        err.telErr = true;
    }
    if (!param.email.match(/^[0-9a-zA-Z_\-]+(\.[0-9a-zA-Z_\-]+)*@([0-9a-zA-Z_\-]+\.)+[a-zA-Z]{2,4}$/)) {
        err.code += 8;
        err.emailErr = true;
    }
    return err;
}

exports.check = check;
exports.ERR_STRs = ERR_STRs;
exports.EXI_STRs = EXI_STRs;